import React from 'react';
import './Valentine.css'; // Optional: Add styling if needed

const valentineLines = [
  "Are you a magician? Because every time I look at you, everyone else disappears. ‚ù§Ô∏è",
  "Do you have a map? Because I just got lost in your eyes. üíò",
  "You're like a Valentine‚Äôs card ‚Äî sweet, charming, and perfect for me. üíå",
  "If kisses were snowflakes, I‚Äôd send you a blizzard. ‚ùÑÔ∏èüòò",
  "Even Cupid would be jealous of how you stole my heart. üèπüíñ"
];

const Valentine = ({ favorites, setFavorites }) => {
  const handleFavorite = (line) => {
    if (!favorites.includes(line)) {
      setFavorites([...favorites, line]);
      alert("‚ù§Ô∏è Added to Favorites!");
    } else {
      alert("Already in favorites!");
    }
  };

  return (
    <div className="container py-5">
      <h2 className="text-center text-danger fw-bold mb-4">
        <i className="bi bi-heart-fill me-2"></i>Valentine's Pick-Up Lines üíò
      </h2>

      <div className="row">
        {valentineLines.map((line, index) => (
          <div key={index} className="col-md-6 col-lg-4 mb-4">
            <div className="card shadow-sm border-0 rounded-4 h-100 bg-light">
              <div className="card-body d-flex flex-column justify-content-between">
                <p className="card-text fs-5">üí¨ {line}</p>
                <button
                  className="btn btn-outline-danger mt-3"
                  onClick={() => handleFavorite(line)}
                >
                  ‚ù§Ô∏è Add to Favorites
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Valentine;
