import React, { useState } from "react";
import { motion } from "framer-motion";
import "bootstrap-icons/font/bootstrap-icons.css";

const Community = () => {
  const [lines, setLines] = useState([
    {
      id: 1,
      text: "Are you a camera? Because every time I look at you, I smile.",
      likes: 3,
      comments: ["So cute!", "I’d use this!"],
    },
    {
      id: 2,
      text: "You must be tired because you’ve been running through my mind all day.",
      likes: 1,
      comments: [],
    },
  ]);

  const [newLine, setNewLine] = useState("");
  const [commentInputs, setCommentInputs] = useState({});
  const [likedIds, setLikedIds] = useState([]); // Track liked for animation

  // Handle Like
  const handleLike = (id) => {
    setLines((prev) =>
      prev.map((line) =>
        line.id === id ? { ...line, likes: line.likes + 1 } : line
      )
    );

    // Trigger pop animation for button
    setLikedIds([...likedIds, id]);
    setTimeout(() => {
      setLikedIds((prev) => prev.filter((likedId) => likedId !== id));
    }, 500);
  };

  // Handle Comment Change
  const handleCommentChange = (id, value) => {
    setCommentInputs({ ...commentInputs, [id]: value });
  };

  // Submit Comment
  const handleCommentSubmit = (id) => {
    const comment = commentInputs[id]?.trim();
    if (!comment) return;

    setLines((prev) =>
      prev.map((line) =>
        line.id === id
          ? { ...line, comments: [...line.comments, comment] }
          : line
      )
    );

    setCommentInputs({ ...commentInputs, [id]: "" });
  };

  // Submit New Pick-up Line
  const handleLineSubmit = (e) => {
    e.preventDefault();
    if (!newLine.trim()) return;

    const newEntry = {
      id: Date.now(),
      text: newLine.trim(),
      likes: 0,
      comments: [],
    };

    setLines([newEntry, ...lines]);
    setNewLine("");
  };

  return (
    <section className="container py-5">
      {/* Header */}
      <motion.div
        className="text-center mb-5"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h2 className="fw-bold text-danger">
          <i className="bi bi-people-fill me-2"></i>
          Community Pick-Up Lines
        </h2>
        <p className="text-muted">
          Share, like, and comment on fun user-generated pick-up lines 💬
        </p>
      </motion.div>

      {/* New Line Form */}
      <motion.form
        onSubmit={handleLineSubmit}
        className="mb-5 d-flex justify-content-center"
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.4 }}
      >
        <div
          className="input-group shadow-lg rounded-pill overflow-hidden"
          style={{ maxWidth: "600px" }}
        >
          <input
            type="text"
            className="form-control border-0 px-3"
            placeholder="✨ Share your flirty genius..."
            value={newLine}
            onChange={(e) => setNewLine(e.target.value)}
          />
          <button className="btn btn-danger px-4 fw-semibold" type="submit">
            Post
          </button>
        </div>
      </motion.form>

      {/* Community Lines */}
      <div className="row g-4">
        {lines.map((line, index) => (
          <motion.div
            key={line.id}
            className="col-md-6 col-lg-4"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
          >
            <motion.div
              whileHover={{ scale: 1.02 }}
              transition={{ type: "spring", stiffness: 200 }}
              className="card h-100 shadow border-0 rounded-4"
            >
              <div className="card-body d-flex flex-column">
                {/* Line Text */}
                <p className="fs-6 mb-3">
                  <span className="fs-5">💬</span> {line.text}
                </p>

               
                <div className="mb-3">
                  <motion.button
                    className="btn btn-sm btn-outline-danger rounded-pill px-3"
                    onClick={() => handleLike(line.id)}
                    animate={
                      likedIds.includes(line.id)
                        ? { scale: [1, 1.3, 1] }
                        : { scale: 1 }
                    }
                    transition={{ duration: 0.4 }}
                  >
                    <i className="bi bi-heart-fill me-1"></i>
                    {line.likes} Likes
                  </motion.button>
                </div>

             
                <div className="mt-auto">
                  <h6 className="text-muted mb-2">
                    <i className="bi bi-chat-dots me-1"></i> Comments
                  </h6>
                  <ul className="list-group list-group-flush small mb-3">
                    {line.comments.length > 0 ? (
                      line.comments.map((comment, idx) => (
                        <li
                          key={idx}
                          className="list-group-item px-0 py-1 border-0 text-secondary"
                        >
                          <i className="bi bi-chat-left-text me-2"></i>
                          {comment}
                        </li>
                      ))
                    ) : (
                      <li className="text-muted small fst-italic">
                        No comments yet. Be the first!
                      </li>
                    )}
                  </ul>

                 
                  <div className="input-group input-group-sm">
                    <input
                      type="text"
                      className="form-control rounded-start"
                      placeholder="Write a comment..."
                      value={commentInputs[line.id] || ""}
                      onChange={(e) =>
                        handleCommentChange(line.id, e.target.value)
                      }
                    />
                    <button
                      className="btn btn-outline-secondary"
                      type="button"
                      onClick={() => handleCommentSubmit(line.id)}
                    >
                      Reply
                    </button>
                  </div>
                </div>
              </div>
            </motion.div>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Community;
