import React from 'react';


const categorizedLines = {
  Funny: [
    "Are you Wi-Fi? Because I'm feeling a connection.",
    "Do you have a pencil? Because I want to erase your past and write our future.",
   
"If we were emojis‚Ä¶ I'd be üòç and you'd be üö© ‚Äî but I‚Äôd still swipe right.",

"Are you Netflix? Because I could watch you for hours and still have no idea what‚Äôs going on.",

"Are you a cat? Because I‚Äôm feline a connection.",
"Are you cereal? Because you're looking like a whole snack with emotional baggage.",
"Are you a salad? Because I don‚Äôt like you, but I know you‚Äôre probably good for me.",
"Are you a traffic light? Because every time I see you, I stop thinking clearly.",
"Is your name ChapStick? Because you‚Äôre on my lips, and slightly awkward.",
"Are you a ghost? Because I‚Äôve been haunted by your last text.",
"Do you work in IT? Because whenever you're around, my logic crashes.",
"Are you an alien? Because your vibe is out of this world‚Ä¶ and a little suspicious.",
"Are you cake? Because I know you're not good for me, but I still want a bite",
"Are you a bug in my code? Because I can‚Äôt stop debugging you.",
"Are you a virus? Because you‚Äôve infected my heart ‚Äî and possibly my system.",
"Do you play Minecraft? Because I just fell into your blocky world.",
"Are you a dad joke? Because I shouldn‚Äôt laugh‚Ä¶ but here we are.",
"Are you Bluetooth? Because I feel paired when you're near.",
"Are you a break statement? Because you make me want to exit my loop.",
"Is your name Lag? Because you're making my heart skip frames."


  ],
  Romantic: [
    "I must be a snowflake, because I've fallen for you.",
    "Every time I see you, my heart skips a beat.",
    
  ],
  Nerdy: [
    "You must be made of quarks and leptons, because you‚Äôre elementary to me.",
    "Are you a JavaScript function? Because you‚Äôve got me in a callback.",
  ],
  Cheesy: [
    "Are you a magician? Because whenever I look at you, everyone else disappears.",
    "If you were a triangle, you‚Äôd be acute one!",
  ]
};

const Library = ({ favorites, setFavorites }) => {
  const handleFavorite = (line) => {
    if (!favorites.includes(line)) {
      setFavorites([...favorites, line]);
      alert("‚ù§Ô∏è Added to Favorites!");
    } else {
      alert("Already in favorites!");
    }
  };

  return (
    <div className="container py-5">
      <h2 className="text-center text-danger fw-bold mb-4">
        <i className="bi bi-book-half me-2"></i>üìö Pick-Up Line Library
      </h2>

      {Object.entries(categorizedLines).map(([category, lines]) => (
        <div key={category} className="mb-5">
          <h4 className="text-primary mb-3">{category} üí¨</h4>
          <div className="row">
            {lines.map((line, idx) => (
              <div key={idx} className="col-md-6 col-lg-4 mb-3">
                <div className="card border-0 shadow-sm rounded-4">
                  <div className="card-body d-flex justify-content-between align-items-center">
                    <p className="mb-0 text-dark">{line}</p>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => handleFavorite(line)}
                    >
                      ‚ù§Ô∏è
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Library;
