import React, { useState } from 'react';
import './ChatAi.css'; // Optional: for styling

const ChatAi = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);

    try {
      const res = await fetch('https://localhost:7280/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input })
      });

      const data = await res.json();
      const aiReply = { sender: 'ai', text: data.reply };

      setMessages((prev) => [...prev, aiReply]);
    } catch (err) {
      setMessages((prev) => [...prev, { sender: 'ai', text: '‚ö†Ô∏è Error getting reply.' }]);
    }

    setInput('');
  };

  return (
    <section id="chat" className="py-5" style={{ background: 'linear-gradient(135deg, #a87cf5ff, #ee5c5cff)' }}>
      <div className="container">
        <div className="card shadow-lg border-0 rounded-4 p-4 mx-auto" style={{ maxWidth: '700px' }}>
          <h3 className="text-center text-danger mb-4">ü§ñ Talk to Chat AI</h3>

          <div id="chatLog" className="mb-4 p-3 border rounded bg-light" style={{ minHeight: '200px', maxHeight: '400px', overflowY: 'auto' }}>
            {messages.map((msg, index) => (
              <div key={index} className={`chat-msg ${msg.sender === 'user' ? 'text-end text-primary' : 'text-start text-success'}`}>
                <span>{msg.text}</span>
              </div>
            ))}
          </div>

          <form onSubmit={sendMessage}>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                placeholder="Type your message..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                required
              />
              <button type="submit" className="btn btn-primary">Send</button>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default ChatAi;
