import React, { useState } from 'react';


const Community = () => {
  const [lines, setLines] = useState([
    {
      id: 1,
      text: "Are you a camera? Because every time I look at you, I smile.",
      likes: 3,
      comments: ["So cute!", "I‚Äôd use this!"],
    },
    {
      id: 2,
      text: "You must be tired because you‚Äôve been running through my mind all day.",
      likes: 1,
      comments: [],
    },
  ]);

  const [newLine, setNewLine] = useState('');
  const [commentInputs, setCommentInputs] = useState({});

  const handleLike = (id) => {
    setLines(
      lines.map((line) =>
        line.id === id ? { ...line, likes: line.likes + 1 } : line
      )
    );
  };

  const handleCommentChange = (id, value) => {
    setCommentInputs({ ...commentInputs, [id]: value });
  };

  const handleCommentSubmit = (id) => {
    const comment = commentInputs[id]?.trim();
    if (!comment) return;
    setLines(
      lines.map((line) =>
        line.id === id
          ? { ...line, comments: [...line.comments, comment] }
          : line
      )
    );
    setCommentInputs({ ...commentInputs, [id]: '' });
  };

  const handleLineSubmit = (e) => {
    e.preventDefault();
    if (!newLine.trim()) return;
    const newEntry = {
      id: Date.now(),
      text: newLine.trim(),
      likes: 0,
      comments: [],
    };
    setLines([newEntry, ...lines]);
    setNewLine('');
  };

  return (
    <div className="container py-5">
      <h2 className="text-center text-danger fw-bold mb-4">
        <i className="bi bi-people-fill me-2"></i>‚ù§Ô∏è Community Pick-Up Lines
      </h2>

      <form onSubmit={handleLineSubmit} className="mb-4">
        <div className="input-group shadow-sm">
          <input
            type="text"
            className="form-control rounded-start"
            placeholder="‚ú® Share your flirty genius..."
            value={newLine}
            onChange={(e) => setNewLine(e.target.value)}
          />
          <button className="btn btn-danger px-4" type="submit">
            Post
          </button>
        </div>
      </form>

      {lines.map((line) => (
        <div key={line.id} className="card mb-3 shadow-sm border-0 rounded-3">
          <div className="card-body">
            <p className="card-text fs-5">üí¨ {line.text}</p>
            <div className="d-flex justify-content-between align-items-center">
              <button
                className="btn btn-sm btn-outline-danger"
                onClick={() => handleLike(line.id)}
              >
                ‚ù§Ô∏è {line.likes} Likes
              </button>
            </div>
            <div className="mt-3">
              <strong className="text-muted">üí≠ Comments</strong>
              <ul className="list-group list-group-flush">
                {line.comments.map((comment, idx) => (
                  <li key={idx} className="list-group-item px-0 py-1 text-secondary">
                    {comment}
                  </li>
                ))}
              </ul>
              <div className="input-group mt-2">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Write a comment..."
                  value={commentInputs[line.id] || ''}
                  onChange={(e) => handleCommentChange(line.id, e.target.value)}
                />
                <button
                  className="btn btn-outline-secondary"
                  onClick={() => handleCommentSubmit(line.id)}
                >
                  Reply
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Community;
